import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Movies.css';
import MovieCard from '../components/MovieCard';
import { faHeart } from '@fortawesome/free-solid-svg-icons';

function Movies() {
  const [likedMovies, setLikedMovies] = useState([]);
  const [topRatedMovies, setTopRatedMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [page, setPage] = useState(1);
  const toggleLike = (movieId) => {
  if (likedMovies.includes(movieId)) {
    // Se o filme já estiver na lista de curtidas, remova-o
    setLikedMovies(likedMovies.filter((id) => id !== movieId));
  } else {
    // Caso contrário, adicione-o à lista de curtidas
    setLikedMovies([...likedMovies, movieId]);
  }
};


  useEffect(() => {
    const apiKey = '3a0f053f7672852c0960041c106db249'; // Substitua pela sua chave da API TMDB

    // Fazer uma solicitação para obter os filmes mais bem avaliados na página atual
    axios
      .get(`https://api.themoviedb.org/3/movie/top_rated?api_key=${apiKey}&page=${page}`)
      .then((response) => {
        const movies = response.data.results.slice(0, 9); // Limitar a 9 filmes iniciais
        setTopRatedMovies((prevMovies) => [...prevMovies, ...movies]);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes mais bem avaliados:', error);
      });
  }, [page]);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    // Lógica de pesquisa de filmes aqui
  };

  const loadMoreMovies = () => {
    setPage((prevPage) => prevPage + 1);
  };

  return (
    <div className="movies-container">
      <div className="header">
        <h1 className="main-title">
          <Link to="/" className="title-link">
            Ver Filmes
          </Link>
        </h1>
        <div>
          {/* Formulário de busca de filmes */}
          <form onSubmit={handleSearchSubmit} className="search-form">
            <input
              type="text"
              placeholder="Buscar filmes..."
              value={searchQuery}
              onChange={handleSearchChange}
            />
            <button type="submit" className="search-button">
              Buscar
            </button>
          </form>
        </div>
      </div>
      <nav>
        <ul>
          <li>
            <Link to="/">Início</Link>
          </li>
          <li>
            <Link to="/filmes">Filmes</Link>
          </li>
        </ul>
      </nav>
      <div className="movie-list">
        {topRatedMovies.map((movie) => (
          <div key={movie.id} className="movie-card">
            <Link to={`/detalhes/${movie.id}`} className="movie-link">
              <img
                src={`https://image.tmdb.org/t/p/w400/${movie.poster_path}`}
                alt={movie.title}
              />
              <h2 className="card-text">{movie.title}</h2>
              <p className="card-text">Rating: {movie.vote_average}</p>
            </Link>
          </div>
        ))}
      </div>
      <div className="load-more">
        <Link to="/" className="load-button">Ver Mais</Link>
      </div>
    </div>
  );
}

export default Movies;
