import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Importe o Link
import './Movies.css'; // Importe seu arquivo CSS de estilo aqui
import MoviesHeader from '../components/MoviesHeader';

function Movies() {
  const [topRatedMovies, setTopRatedMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const apiKey = '3a0f053f7672852c0960041c106db249'; // Substitua pela sua chave da API TMDB

    // Fazer uma solicitação para obter os filmes mais bem avaliados
    axios
      .get(`https://api.themoviedb.org/3/movie/top_rated?api_key=${apiKey}`)
      .then((response) => {
        const movies = response.data.results.slice(0, 9); // Limitar a 9 filmes iniciais
        setTopRatedMovies(movies);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes mais bem avaliados:', error);
      });
  }, []);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    // Aqui você pode adicionar a lógica para buscar filmes com base na consulta de pesquisa
  };

  return (
    <div className="home-container">
      <div className="header">
        <h1 className="main-title">
          <Link to="/" className="title-link">
            Ver Filmes
          </Link>
        </h1>
        <div>
          {/* Formulário de busca de filmes */}
          <form onSubmit={handleSearchSubmit} className="search-form">
            <input
              type="text"
              placeholder="Buscar filmes..."
              value={searchQuery}
              onChange={handleSearchChange}
            />
            <button type="submit" className="search-button">
              Buscar
            </button>
          </form>
        </div>
      </div>
      <nav>
        <ul>
          <li>
            <Link to="/">Início</Link>
          </li>
          <li>
            <Link to="/filmes">Filmes</Link>
          </li>
          <li>
            <Link to="/filmes-favoritos">Filmes Favoritos</Link>
          </li>
          <li>
            <Link to="/configuracoes">Configurações</Link>
          </li>
          <li>
            <Link to="/sair">Sair</Link>
          </li>
          
        </ul>
      </nav>
      <div className="movie-list">
        {popularMovies.map((movie) => (
          <Link key={movie.id} to={`/detalhes/${movie.id}`} className="movie-link">
            <MovieCard movie={movie} />
          </Link>
        ))}
      </div>
      <div className="load-more">
        <button onClick={loadMoreMovies} className="load-button">
          Ver Mais
        </button>
      </div>
    </div>
  );
}

export default Movies;
