import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';
import MovieCard from '../components/MovieCard';
import FavoriteCarousel from '../components/FavoriteCarousel'; // Importe o FavoriteCarousel

function Home() {
  const [popularMovies, setPopularMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [favoriteMovies, setFavoriteMovies] = useState([]); // Estado para os filmes favoritos

  useEffect(() => {
    // Chave da API TMDB (substitua pela sua chave)
    const apiKey = '3a0f053f7672852c0960041c106db249';

    // Fazer uma solicitação para obter a lista de filmes populares
    axios
      .get(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}`)
      .then((response) => {
        const movies = response.data.results.slice(0, 9); // Limitar a 9 filmes iniciais
        setPopularMovies(movies);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes populares:', error);
      });
  }, []);

  // Restante do código para manipular pesquisa e carregar mais filmes permanece o mesmo

  // Função para adicionar um filme aos favoritos
  const addFavoriteMovie = (movie) => {
    setFavoriteMovies((prevFavorites) => [...prevFavorites, movie]);
  };

  // Função para remover um filme dos favoritos
  const removeFavoriteMovie = (movie) => {
    setFavoriteMovies((prevFavorites) =>
      prevFavorites.filter((favMovie) => favMovie.id !== movie.id)
    );
  };

  return (
    <div className="home-container">
      {/* Coloque o carrossel de favoritos no topo da página */}
      <FavoriteCarousel
        favoriteMovies={favoriteMovies}
        onRemoveFavorite={removeFavoriteMovie} // Passe a função para remover filmes favoritos
      />

      <div className="header">
        <h1 className="main-title">
          <Link to="/" className="title-link">
            Ver Filmes
          </Link>
        </h1>
        <div>
          {/* Formulário de busca de filmes */}
          <form onSubmit={handleSearchSubmit} className="search-form">
            <input
              type="text"
              placeholder="Buscar filmes..."
              value={searchQuery}
              onChange={handleSearchChange}
            />
            <button type="submit" className="search-button">
              Buscar
            </button>
          </form>
        </div>
      </div>
      <nav>
        <ul>
          <li>
            <Link to="/">Início</Link>
          </li>
          <li>
            <Link to="/filmes">Filmes</Link>
          </li>

          <li>
            <Link to="/configuracoes">Configurações</Link>
          </li>
          <li>
            <Link to="/sair">Sair</Link>
          </li>
        </ul>
      </nav>
      <div className="movie-list">
        {popularMovies.map((movie) => (
          <Link key={movie.id} to={`/detalhes/${movie.id}`} className="movie-link">
            <MovieCard
              movie={movie}
              onAddFavorite={addFavoriteMovie} // Passe a função para adicionar filmes favoritos
            />
          </Link>
        ))}
      </div>
      <div className="load-more">
        <button onClick={loadMoreMovies} className="load-button">
          Ver Mais
        </button>
      </div>
    </div>
  );
}

export default Home;
