import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './pages/Filmes.css'; // Estilo específico para a página de filmes

function Filmes() {
  const [popularMovies, setPopularMovies] = useState([]);

  useEffect(() => {
    // Chave da API TMDB (substitua pela sua chave)
    const apiKey = '3a0f053f7672852c0960041c106db249';

    // Fazer uma solicitação para obter uma lista de filmes diferentes (por exemplo, filmes em destaque)
    axios
      .get(`https://api.themoviedb.org/3/movie/top_rated?api_key=${apiKey}`)
      .then((response) => {
        const movies = response.data.results.slice(0, 9); // Pegar os 10 primeiros filmes
        setPopularMovies(movies);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes em destaque:', error);
      });
  }, []);

  return (
    <div>
      <h1 style={{ marginTop: '1%', marginBottom: '0' }}>
        <Link to="/" style={{ textDecoration: 'none', fontFamily: 'Arial, Helvetica, sans-serif' }}>
          Ver Filmes
        </Link>
      </h1>
      <nav>
        <ul>
          <li>
            <Link to="/">Início</Link>
          </li>
          <li>
            <Link to="/filmes">Filmes</Link>
          </li>
          <li>
            <Link to="/filmes-favoritos">Filmes Favoritos</Link>
          </li>
          <li>
            <Link to="/configuracoes">Configurações</Link>
          </li>
          <li>
            <Link to="/sair">Sair</Link>
          </li>
        </ul>
      </nav>
      <div className="movie-list">
        {popularMovies.map((movie) => (
          <Link key={movie.id} to={`/detalhes/${movie.id}`} style={{ textDecoration: 'none' }}>
            {/* Renderizar os filmes diferentes aqui */}
            <div className="movie-card">
              <img
                src={`https://image.tmdb.org/t/p/w300/${movie.poster_path}`}
                alt={movie.title}
              />
              <h3>{movie.title}</h3>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Filmes;
