import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import './MovieCard.css';

function MovieCard({ movie, likedMovies, toggleLike }) {
  const isLiked = likedMovies.includes(movie.id);

  const toggleLikeClick = (e) => {
    e.preventDefault();
    toggleLike(movie.id);
  };

  const heartIconStyles = {
    position: 'absolute',
    top: '10px', // Ajuste a distância do topo conforme necessário
    right: '10px', // Ajuste a distância da direita conforme necessário
    color: isLiked ? 'red' : 'black',
    cursor: 'pointer',
    zIndex: 1, // Certifique-se de que o ícone do coração esteja acima do conteúdo
    fontSize: '24px', // Aumenta o tamanho do ícone em 2 pixels
  };

  return (
    <div className="movie-card" style={{ position: 'relative' }}>
      <FontAwesomeIcon
        icon={faHeart}
        style={heartIconStyles}
        onClick={toggleLikeClick}
      />
      <img
        src={`https://image.tmdb.org/t/p/w400/${movie.poster_path}`}
        alt={movie.title}
      />
      <h2 className='card-text'>{movie.title}</h2>
      <p className='card-text'>Rating: {movie.vote_average}</p>
    </div>
  );
}

export default MovieCard;
