import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Movies.css'; // Certifique-se de criar este arquivo CSS para estilizar a página
import MovieCard from '../components/MovieCard';

function Movies() {
  const [topRatedMovies, setTopRatedMovies] = useState([]);

  useEffect(() => {
    // Chave da API TMDB (substitua pela sua chave)
    const apiKey = '3a0f053f7672852c0960041c106db249';

    // Fazer uma solicitação para obter os filmes Top Rated
    axios
      .get(`https://api.themoviedb.org/3/movie/top_rated?api_key=${apiKey}`)
      .then((response) => {
        const movies = response.data.results.slice(0, 9); // Limitar a 9 filmes iniciais
        setTopRatedMovies(movies);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes Top Rated:', error);
      });
  }, []);

  return (
    <div className="movies-container">
      <div className="header">
        <h1 className="main-title">Filmes Top Rated</h1>
        <div>
          <Link to="/" className="back-link">
            Voltar para a Home
          </Link>
        </div>
      </div>
      <div className="movie-list">
        {topRatedMovies.map((movie) => (
          <Link key={movie.id} to={`/detalhes/${movie.id}`} className="movie-link">
            <MovieCard movie={movie} />
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Movies;
