import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';

function Home() {
  const [favoriteMovies, setFavoriteMovies] = useState([]);

  useEffect(() => {
    // Chave da API TMDB (substitua pela sua chave)
    const apiKey = '3a0f053f7672852c0960041c106db249';

    // Fazer uma solicitação para obter a lista de filmes populares
    axios
      .get(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}`)
      .then((response) => {
        const movies = response.data.results.slice(0, 0); // Pegar os 3 primeiros filmes
        setFavoriteMovies(movies);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes favoritos:', error);
      });
  }, []);

  return (
    <div>
      <h1>Ver Filmes</h1> {/* Movido para o topo */}
      <div className="movies-box">
        <button>
          <Link to="/filmes">Filmes</Link>
        </button>
        <h2>Filmes Favoritos</h2>
        <ul>
          {favoriteMovies.map((movie) => (
            <li key={movie.id}>
              <Link to={`/detalhes/${movie.id}`}>{movie.title}</Link>
            </li>
          ))}
        </ul>
      </div>
      <nav>
        <ul>
          <li>
            <Link to="/">Início</Link>
          </li>
          <li>
            <Link to="/configuracoes">Configurações</Link>
          </li>
          <li>
            <Link to="/sair">Sair</Link>
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default Home;
