import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from "./pages/Home";
import Header from "./components/Header";
import Watched from "./components/Watched";
import Add from "./components/Add";
import MovieDetails from "./pages/MovieDetails"; // Importe o componente MovieDetails
import "./App.css";
import '@fortawesome/fontawesome-free/css/all.min.css';
import { GlobalProvider } from './context/GlobalState';
import Movies from './pages/Movies';

function App() {
  return (
    <GlobalProvider>
      <Router>
        <Header />
        
        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/filmes" component={Movies} /> {/* Rota para a p√°gina Movies */}
          <Route path="/add" component={Add} />
          <Route path="/watched" component={Watched} />
          <Route path="/detalhes/:id" component={MovieDetails} />
          <Route path="/movie/:id" component={MovieDetails} /> {/* Rota para os detalhes do filme */}
        </Switch>
      </Router>
    </GlobalProvider>
  );
}

export default App;
