import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';

function MovieCard({ movie, favoriteMovies, setFavoriteMovies }) {
  const isFavorite = favoriteMovies.some((favorite) => favorite.id === movie.id);

  const toggleLike = () => {
    if (isFavorite) {
      setFavoriteMovies((prevFavorites) =>
        prevFavorites.filter((favorite) => favorite.id !== movie.id)
      );
    } else {
      setFavoriteMovies((prevFavorites) => [...prevFavorites, movie]);
    }
  };

  const heartIconStyles = {
    color: isFavorite ? 'red' : 'black',
    cursor: 'pointer',
    fontSize: '24px',
  };

  return (
    <div className="movie-card">
      <div style={{ position: 'relative' }}>
        <FontAwesomeIcon
          icon={faHeart}
          style={heartIconStyles}
          onClick={toggleLike}
        />
      </div>
      <img
        src={`https://image.tmdb.org/t/p/w400/${movie.poster_path}`}
        alt={movie.title}
      />
      <h2 className="card-text">{movie.title}</h2>
      <p className="card-text">Rating: {movie.vote_average}</p>
      {isFavorite ? (
        <button onClick={toggleLike}>Remover dos Favoritos</button>
      ) : (
        <button onClick={toggleLike}>Adicionar aos Favoritos</button>
      )}
    </div>
  );
}

export default MovieCard;
