import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function MovieDetails() {
  const { id } = useParams();
  const [movieDetails, setMovieDetails] = useState(null);
  const [cast, setCast] = useState([]);
  const [showAllCast, setShowAllCast] = useState(false);

  useEffect(() => {
    const apiKey = '3a0f053f7672852c0960041c106db249';

    axios
      .get(`https://api.themoviedb.org/3/movie/${id}?api_key=${apiKey}&language=pt-BR`)
      .then((response) => {
        setMovieDetails(response.data);

        axios
          .get(`https://api.themoviedb.org/3/movie/${id}/credits?api_key=${apiKey}`)
          .then((castResponse) => {
            setCast(castResponse.data.cast);
          })
          .catch((castError) => {
            console.error('Erro ao buscar informações de elenco:', castError);
          });
      })
      .catch((error) => {
        console.error('Erro ao buscar detalhes do filme:', error);
      });
  }, [id]);

  if (!movieDetails) {
    return <div>Carregando...</div>;
  }

  const textStyle = {
    textAlign: 'center',
    color: '#fff',
  };

  const buttonStyle = {
    backgroundColor: 'transparent',
    border: '1px solid #fff',
    color: '#000',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
  };

  const limitedCast = showAllCast ? cast : cast.slice(0, 5);

  // Mapeando nomes dos atores para ícones do Font Awesome
  const castIcons = {
    'Nome do Ator 1': 'fa-icon-name-1',
    'Nome do Ator 2': 'fa-icon-name-2',
    // Adicione mais nomes e ícones conforme necessário
  };

  // Ícone de estrela do Font Awesome para o rating
  const starIcon = <i className="fas fa-star"></i>;

  return (
    <div className="movie-details-container">
      <h1>{movieDetails.title}</h1>
      <p style={textStyle}>Data de Lançamento: {movieDetails.release_date}</p>
      <p style={textStyle}>Duração: {movieDetails.runtime} minutos</p>
      <p style={textStyle}>
        {starIcon} Avaliação: {movieDetails.vote_average}
      </p>
      {/* Adicione o elenco aqui */}
    </div>
  );
}

export default MovieDetails;
