import React, { createContext, useReducer, useContext } from 'react';
import { reducer, initialState } from '../components/Reducer'; // Ajuste o caminho conforme necessário

// 1. Crie um contexto
const GlobalContext = createContext();

// 2. Crie um provedor que envolverá sua aplicação
export const GlobalProvider = ({ children }) => {
  // 3. Defina o estado global e as funções de ação aqui usando useReducer ou useState, dependendo das suas necessidades.
  const [state, dispatch] = useReducer(reducer, initialState);

  // 4. Adicione a função add_movie ao reducer
  const add_movie = (movie) => {
    dispatch({ type: 'ADD_MOVIE', payload: movie });
  };

  // 5. Retorne o contexto e os estados e funções de ação necessários
  return (
    <GlobalContext.Provider value={{ state, add_movie }}>
      {children}
    </GlobalContext.Provider>
  );
};

// 6. Crie um hook personalizado para usar o contexto em seus componentes
export const useGlobalState = () => {
  const context = useContext(GlobalContext);
  if (!context) {
    throw new Error('useGlobalState deve ser usado dentro de um GlobalProvider');
  }
  return context;
};
