// Movies.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Movies.css'; // Importe seu arquivo CSS de estilo aqui

function Movies() {
  const [topRatedMovies, setTopRatedMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const apiKey = '3a0f053f7672852c0960041c106db249'; // Substitua pela sua chave da API TMDB

    // Fazer uma solicitação para obter os filmes mais bem avaliados
    axios
      .get(`https://api.themoviedb.org/3/movie/top_rated?api_key=${apiKey}`)
      .then((response) => {
        const movies = response.data.results.slice(0, 9); // Limitar a 9 filmes iniciais
        setTopRatedMovies(movies);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes mais bem avaliados:', error);
      });
  }, []);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    // Aqui você pode adicionar a lógica para buscar filmes com base na consulta de pesquisa
  };

  return (
    <div className="movies-container">
      <div className="navigation-links">
        {/* Link para a página Início */}
        <Link to="/">Início</Link>
        {/* Link para a própria página de Filmes */}
        <Link to="/filmes">Filmes</Link>
      </div>
      </div>>
    <div className="movies-container">
      <h1 className="main-title">Filmes Mais Bem Avaliados</h1>
      <form onSubmit={handleSearchSubmit} className="search-form">
        <input
          type="text"
          placeholder="Buscar filmes..."
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type="submit" className="search-button">
          Buscar
        </button>
      </form>
      
      <div className="movie-list">
        {topRatedMovies.map((movie) => (
          <div key={movie.id} className="movie-card">
            <img
              src={`https://image.tmdb.org/t/p/w400/${movie.poster_path}`}
              alt={movie.title}
            />
            <h2 className="card-text">{movie.title}</h2>
            <p className="card-text">Rating: {movie.vote_average}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Movies;
