import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart as heartEmpty } from '@fortawesome/free-regular-svg-icons'; // Ícone vazio
import { faHeart as heartSolid } from '@fortawesome/free-solid-svg-icons'; // Ícone sólido
import './MovieCard.css';

function MovieCard({ movie }) {
  const [isLiked, setIsLiked] = useState(false);

  const toggleLike = () => {
    setIsLiked(!isLiked);
  };

  return (
    <div className="movie-card">
      <img
        src={`https://image.tmdb.org/t/p/w400/${movie.poster_path}`}
        alt={movie.title}
      />
      <h2 className='card-text'>{movie.title}</h2>
      <p className='card-text'>Rating: {movie.vote_average}</p>
      <div className="heart-icon-container" onClick={toggleLike}>
        <FontAwesomeIcon
          icon={isLiked ? heartSolid : heartEmpty} // Usa o ícone vazio ou sólido com base no estado
          className={`heart-icon ${isLiked ? 'liked' : ''}`}
        />
      </div>
    </div>
  );
}

export default MovieCard;
