import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function MovieDetails() {
  const { id } = useParams();
  const [movieDetails, setMovieDetails] = useState(null);
  const [showAllCast, setShowAllCast] = useState(false);

  useEffect(() => {
    const apiKey = '3a0f053f7672852c0960041c106db249';

    // Primeira solicitação para obter detalhes do filme
    axios
      .get(`https://api.themoviedb.org/3/movie/${id}?api_key=${apiKey}&language=pt-BR`)
      .then((response) => {
        setMovieDetails(response.data);
      })
      .catch((error) => {
        console.error('Erro ao buscar detalhes do filme:', error);
      });
  }, [id]);

  if (!movieDetails) {
    return <div>Carregando...</div>;
  }

  // Estilo para o texto dos detalhes do filme
  const textStyle = {
    textAlign: 'center',
    color: '#fff', // Cor do texto (branco)
  };

  // Estilo para o botão "Ver Mais"
  const buttonStyle = {
    backgroundColor: 'transparent',
    border: '2px solid #fff',
    color: '#fff', // Cor do texto (preto)
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
  };

  // Componente de ícone de estrela
  const StarIcon = () => <i className="fas fa-star" style={{ marginRight: '5px' }}></i>;

  return (
    <div className="movie-details-container">
      <h1>{movieDetails.title}</h1>
      <img
        src={`https://image.tmdb.org/t/p/w300/${movieDetails.poster_path}`}
        alt={movieDetails.title}
      />
      <p style={textStyle}>Data de Lançamento: {movieDetails.release_date}</p>
      <p style={textStyle}>Duração: {movieDetails.runtime} minutos</p>
      <p style={textStyle}>
        <StarIcon /> Avaliação: {movieDetails.vote_average}
      </p>
      <p style={textStyle}>Sinopse: {movieDetails.overview}</p>

      {/* Botão "Ver Mais" */}
      {!showAllCast && (
        <button onClick={() => setShowAllCast(true)} style={buttonStyle}>
          Ver Mais
        </button>
      )}
    </div>
  );
}

export default MovieDetails;
