import React from 'react';
import Slider from 'react-slick';
import './FavoriteCarousel.css'; // Importe o CSS para estilizar o carrossel

function FavoriteCarousel({ favoriteMovies }) {
  // Configuração do carrossel (você pode ajustar isso conforme necessário)
  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  return (
    <div className="favorite-carousel-container">
      <h2>Favoritos</h2>
      <Slider {...settings}>
        {favoriteMovies.map((movie) => (
          <div key={movie.id} className="favorite-movie-card">
            {/* Renderize os detalhes do filme, como título, imagem, etc. */}
            <img
              src={`https://image.tmdb.org/t/p/w400/${movie.poster_path}`}
              alt={movie.title}
            />
            <h3>{movie.title}</h3>
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default FavoriteCarousel;
