import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';
import MovieCard from '../components/MovieCard';

function Home() {
  const [popularMovies, setPopularMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [favoriteMovies, setFavoriteMovies] = useState([]); // Estado para filmes favoritos

  useEffect(() => {
    // Chave da API TMDB (substitua pela sua chave)
    const apiKey = '3a0f053f7672852c0960041c106db249';

    // Fazer uma solicitação para obter a lista de filmes populares
    axios
      .get(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}`)
      .then((response) => {
        const movies = response.data.results.slice(0, 9); // Limitar a 9 filmes iniciais
        setPopularMovies(movies);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes populares:', error);
      });
  }, []);

  // Função para lidar com a mudança na consulta de pesquisa
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Função para lidar com o envio do formulário de pesquisa (não implementa filtro ainda)
  const handleSearchSubmit = (event) => {
    event.preventDefault();
    // Aqui você pode adicionar a lógica para filtrar os filmes com base na consulta de pesquisa
  };

  // Função para carregar mais filmes
  const loadMoreMovies = () => {
    const apiKey = '3a0f053f7672852c0960041c106db249';
    const nextPage = popularMovies.length / 9 + 1; // Próxima página de filmes

    // Fazer uma solicitação para obter a próxima página de filmes populares
    axios
      .get(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&page=${nextPage}`)
      .then((response) => {
        const newMovies = response.data.results;
        setPopularMovies((prevMovies) => [...prevMovies, ...newMovies]);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes populares:', error);
      });
  };

  // Função para adicionar ou remover filmes favoritos
  const handleToggleFavorite = (movie) => {
    // Verifique se o filme já está na lista de favoritos
    const isFavorite = favoriteMovies.some((favMovie) => favMovie.id === movie.id);

    if (isFavorite) {
      // Se o filme já é um favorito, remova-o da lista de favoritos
      setFavoriteMovies((prevFavorites) =>
        prevFavorites.filter((favMovie) => favMovie.id !== movie.id)
      );
    } else {
      // Se o filme não é um favorito, adicione-o à lista de favoritos
      setFavoriteMovies((prevFavorites) => [...prevFavorites, movie]);
    }
  };

  return (
    <div className="home-container">
      <div className="header">
        <h1 className="main-title">
          <Link to="/" className="title-link">
            Ver Filmes
          </Link>
        </h1>
        <div>
          {/* Formulário de busca de filmes */}
          <form onSubmit={handleSearchSubmit} className="search-form">
            <input
              type="text"
              placeholder="Buscar filmes..."
              value={searchQuery}
              onChange={handleSearchChange}
            />
            <button type="submit" className="search-button">
              Buscar
            </button>
          </form>
        </div>
      </div>
      <nav>
        <ul>
          <li>
            <Link to="/">Início</Link>
          </li>
          <li>
            <Link to="/filmes">Filmes</Link>
          </li>
          <li>
            <Link to="/configuracoes">Configurações</Link>
          </li>
          <li>
            <Link to="/sair">Sair</Link>
          </li>
        </ul>
      </nav>
      <div className="movie-list">
        {popularMovies.map((movie) => (
          <Link key={movie.id} to={`/detalhes/${movie.id}`} className="movie-link">
            <MovieCard
              movie={movie}
              isFavorite={favoriteMovies.some((favMovie) => favMovie.id === movie.id)}
              onToggleFavorite={handleToggleFavorite}
            />
          </Link>
        ))}
      </div>
      <div className="load-more">
        <button onClick={loadMoreMovies} className="load-button">
          Ver Mais
        </button>
      </div>
    </div>
  );
}

export default Home;
