import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../pages/Home.css'; // Corrija o caminho para o estilo da Home
import Movies from './Movies';

function Home() {
  const [popularMovies, setPopularMovies] = useState([]);
  const [visibleMovies, setVisibleMovies] = useState(9); // Quantidade inicial de filmes visíveis
  const [searchTerm, setSearchTerm] = useState(''); // Estado para o termo de pesquisa

  const apiKey = '3a0f053f7672852c0960041c106db249';

  useEffect(() => {
    // Fazer uma solicitação para obter a lista de filmes populares
    axios
      .get(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}`)
      .then((response) => {
        const movies = response.data.results; // Pegar todos os filmes
        setPopularMovies(movies);
      })
      .catch((error) => {
        console.error('Erro ao buscar filmes populares:', error);
      });
  }, []);

  // Função para carregar mais filmes
  const loadMoreMovies = () => {
    setVisibleMovies((prevVisibleMovies) => prevVisibleMovies + 9); // Carregar mais 9 filmes
  };

  // Função para atualizar o estado do termo de pesquisa
  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <div>
      <h1 style={{ marginTop: '1%', marginBottom: '0' }}>
        <Link to="/" style={{ textDecoration: 'none', fontFamily: 'Arial, Helvetica, sans-serif' }}>
          Ver Filmes
        </Link>
      </h1>
      <nav>
        <ul>
          <li>
            <Link to="/">Início</Link>
          </li>
          <li>
            <Link to="/filmes">Filmes</Link>
          </li>
          <li>
            <Link to="/filmes-favoritos">Filmes Favoritos</Link>
          </li>
          <li>
            <Link to="/configuracoes">Configurações</Link>
          </li>
          <li>
            <Link to="/sair">Sair</Link>
          </li>
        </ul>
      </nav>

      {/* Área de busca */}
      <div className="search-area">
        <input
          type="text"
          placeholder="Buscar filmes..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
        <button>Buscar</button>
      </div>

      <div className="movie-list">
        {/* Usando o componente Movies e passando a prop 'movies' com base no estado 'visibleMovies' */}
        <Movies movies={popularMovies.slice(0, visibleMovies)} />
      </div>
      {visibleMovies < popularMovies.length && ( // Mostrar o botão "Ver Mais" se houver mais filmes para carregar
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <button onClick={loadMoreMovies}>Ver Mais</button>
        </div>
      )}
    </div>
  );
}

export default Home;
