import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';

function MovieCard({ movie }) {
  const [isLiked, setIsLiked] = useState(false);

  const toggleLike = () => {
    setIsLiked(!isLiked);
  };

  const heartIconStyles = {
    color: isLiked ? 'red' : 'black', // Define a cor com base no estado isLiked
    cursor: 'pointer', // Altera o cursor quando o ícone for clicável
  };

  return (
    <div className="movie-card">
      <img
        src={`https://image.tmdb.org/t/p/w400/${movie.poster_path}`}
        alt={movie.title}
      />
      <h2 className='card-text'>{movie.title}</h2>
      <p className='card-text'>Rating: {movie.vote_average}</p>
      <div className="heart-icon-container">
        <FontAwesomeIcon
          icon={faHeart}
          style={heartIconStyles} // Aplica os estilos diretamente ao ícone
          onClick={toggleLike}
        />
      </div>
    </div>
  );
}

export default MovieCard;
