// Reducer.jsx

// Defina um tipo de ação (pode ter vários tipos)
export const actionTypes = {
  ADD_MOVIE: 'ADD_MOVIE',
  REMOVE_MOVIE: 'REMOVE_MOVIE',
};

// O estado inicial
export const initialState = {
  movies: [], // Inicialmente, não há filmes
};

// O redutor real
export const reducer = (state, action) => {
  switch (action.type) {
    case actionTypes.ADD_MOVIE:
      return {
        ...state,
        movies: [...state.movies, action.payload], // Adicionar um filme ao estado
      };
    case actionTypes.REMOVE_MOVIE:
      return {
        ...state,
        movies: state.movies.filter((movie) => movie.id !== action.payload), // Remover um filme com base no ID
      };
    default:
      return state; // Retorna o estado inalterado por padrão
  }
};
